db:select.db:parameterized-query:
SELECT documento, tipodoc, nombre, apellido, "user", password, idcrm, idfacturacion
	FROM public.cliente
	 WHERE documento=#[message.inboundProperties.'http.query.params'.number] AND tipodoc=#[message.inboundProperties.'http.query.params'.type];

dw:transform-message.dw:set-payload:
%dw 1.0
%output application/json
---
{
	code: "200",
    status: "The contact has been found.",
	response:{
	customer: {
		identification: {
			type: payload[0].tipodoc,
			number: payload[0].documento
		},
		aplicacion:{

    	user:payload[0].user,
    	password:payload[0].password

    },
		personal: {
			firstName: payload[0].nombre,
			lastName: payload[0].apellido
		}
	},
	idcrm:payload[0].idcrm,
	idinvoice:payload[0].idfacturacion
}}
